package com.operations;

public class Switchdays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String v = ("sunday");
		switch (v) {
		case ("monday"):

		case ("tuesday"):

		case ("wednesday"):

		case ("thursday"):

		case ("friday"):
			System.out.println("working day");
			break;
		case ("saturday"):

		case ("sunday"):
			System.out.println("holiday");
			break;
		default:
			System.out.println("nothing");

		}

	}
}
