package com.operations;

public class While2468 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=0;
		int b;
		while (a<=8)
		{
			b=a+1;
			System.out.println(b);
			a++;
			a++;
		}
		int c =1;
		int d;
		while (c<=8)
		{
			d=c+1;
			System.out.println(d);
			c++;
			c++;
		}
	
}}
